				valid: false,
				touched: false,
				validationMessage:'',
				showLabel: true
			},
		}
  	}

  	render() {
    	return (
      		<div>
        		My UpdateSiteInfo
      		</div>
    	)
  	}
}

const mapStateToProps = (state) => {
	return {
		site: state.site
	}
}

export default connect(mapStateToProps)(UpdateSiteInfo)
