import React, { Component } from 'react';
import PageTop from '../Utils/PageTop';
import { frets, price } from '../Utils/Form/fixedCategories';
import CollapseCheckbox from '../Utils/CollapseCheckbox.js';
import CollapseRadio from '../Utils/CollapseRadio.js';

import { connect } from 'react-redux';
import { getBrands, getWoods } from '../../actions/products_action';



class Shop extends Component {
    state = {
        gird: '',
        limit: 6,
        skip: 0,
        filters: {
            brand: [],
            frets: [],
            wood: [],
            price: []
        }
    }
	componentDidMount() {
		this.props.dispatch(getBrands());
		this.props.dispatch(getWoods());
	}

    handleFilters = (filters, category) => {
        const newFilters = {...this.state.filters}
        newFilters[category] = filters;

        if(category === 'price')
        {
            let priceVal = this.handlePrice(filters);
            newFilters[category] = priceVal;
        }
        this.setState({
            filters: newFilters
        })
        console.log(this.state)
    } 

    render() {
      	const products = this.props.products;
        return (

        	<div>
        		<PageTop title='Browse products'/>
        		<div className='container'>
        			<div className='shop_wrapper'>
        				<div className='left'>
        					<CollapseCheckbox 
                                initState={true}
                                title='Brands'
                                list={products.brands}
                                handleFilters={(filters)=> this.handleFilters(filters, 'brand')}
                            />
                            <CollapseCheckbox 
                                initState={false}
                                title='Frets'
                                list={frets}
                                handleFilters={(filters)=> this.handleFilters(filters, 'Frets')}
                            />
                            <CollapseCheckbox 
                                initState={true}
                                title='Woods'
                                list={products.woods}
                                handleFilters={(filters)=> this.handleFilters(filters, 'Woods')}
                            />
                            <CollapseRadio 
                                initState={true}
                                title='Price'
                                list={price}
                                handleFilters={(filters)=> this.handleFilters(filters, 'Price')}
                            />
        				</div>
        				<div className='right'>
        					right
        				</div>
        			</div>
        		</div>
        	</div>
    )
  }
}

const mapStateToProps = (state) => {
	return {
		products: state.products
	}
}

export default connect(mapStateToProps)(Shop);
