import React from 'react';

const ProductBlock = ({products, type, removeItem}) => {

	const renderCartImg = (images) => {
		if(images.length > 0) {
			console.log(images[0].url)
			return images[0].url
		}
		else {
			return '/images/image_not_available'
		}
	};

	const renderItems = () => (
		products.cartDetail ?
			products.cartDetail.map(products => (
				<div key={products._id} className='user_product_block'> 
					<div
						className='image'
						style={{
				      	 	background:`url(${renderCartImg(products.images)}) no-repeat`
				      	 }}
					>

					</div>
					<div> {products.name} </div>
				</div>

			))
		: null
	)

	return (
	    <div>
	      { renderItems() }
	    </div>

	)
}
export default ProductBlock;
