import React, { Component } from 'react';
import PageTop from '../Utils/PageTop';
import { frets, price } from '../Utils/Form/fixedCategories';
import CollapseCheckbox from '../Utils/CollapseCheckbox.js';
import CollapseRadio from '../Utils/CollapseRadio.js';
import LoadMoreCards from './LoadMoreCards';

import { connect } from 'react-redux';
import { getProductsToShop, getBrands, getWoods } from '../../actions/products_action';


class Shop extends Component {
    state = {
        gird: '',
        limit: 6,
        skip: 0,
        filters: {
            brand: [],
            frets: [],
            wood: [],
            price: []
        }
    }
	componentDidMount() {
		this.props.dispatch(getBrands());
		this.props.dispatch(getWoods());
        this.props.dispatch(getProductsToShop(this.state.skip, this.state.limit, this.state.filters));
        console.log(this.props.products);
	}

    handlePrice = (value) => {
        const data = price;
        let array = [];
        for(let key in data)
        {
            if(data[key]._id === parseInt(value,10)) {
                array = data[key].array
            }
        }
        return array;
    }

    handleFilters = (filters, category) => {
        const newFilters = {...this.state.filters}
        newFilters[category] = filters;

        if(category === 'price')
        {
            let priceVal = this.handlePrice(filters);
            newFilters[category] = priceVal;
        }
        this.showFilteredResults(newFilters);
        this.setState({
            filters: newFilters
        })

    } 

    showFilteredResults = (filters) => {
        this.props.dispatch(getProductsToShop(0, this.state.limit, filters)).then(() => {
            this.setState({
                skip: 0

            })
        });

        console.log(this.state)

    }

    LoadMoreProducts = () => {
        let skip = this.state.skip + this.state.limit;
        console.lou(skip)
        this.props.dispatch(getProductsToShop(
            skip,
            this.state.limit,
            this.state.filters,
            this.props.products.toShop
        )).then(() => {
            this.setState({
                skip: skip
            })
        })
    }

    render() {
      	const products = this.props.products;
        return (

        	<div>
        		<PageTop title='Browse products'/>
        		<div className='container'>
        			<div className='shop_wrapper'>
        				<div className='left'>
        					<CollapseCheckbox 
                                initState={true}
                                title='Brands'
                                list={products.brands}
                                handleFilters={(filters)=> this.handleFilters(filters, 'brand')}
                            />
                            <CollapseCheckbox 
                                initState={false}
                                title='Frets'
                                list={frets}
                                handleFilters={(filters)=> this.handleFilters(filters, 'frets')}
                            />
                            <CollapseCheckbox 
                                initState={true}
                                title='Woods'
                                list={products.woods}
                                handleFilters={(filters)=> this.handleFilters(filters, 'woods')}
                            />
                            <CollapseRadio 
                                initState={true}
                                title='Price'
                                list={price}
                                handleFilters={(filters)=> this.handleFilters(filters, 'price')}
                            />
        				</div>
        				<div className='right'>
        					<LoadMoreCards
                                grid={this.state.grid}
                                limit={this.state.limit}
                                size={products.toShopSize}
                                products={products.toShop}
                                loadMore={() => this.LoadMoreProducts()}
                            />
        				</div>
        			</div>
        		</div>
        	</div>
    )
  }
}

const mapStateToProps = (state) => {
	return {
		products: state.products
	}
}

export default connect(mapStateToProps)(Shop);
