import React, { Component } from 'react';
import FormField from '../../Utils/Form/FormField';
import { update, generateData, isFormValid, populateFields } from '../../Utils/Form/FormAction';
import { updateInfo, clearUpdateUser } from '../../../actions/user_action';
import { connect } from 'react-redux';

class UpdateSiteInfo extends Component {
	state = {
  	formError: false,
		formSuccess: '',
		formData: {
			address: {
				element: 'input',
				value: '',
				config: {
					label: 'Address',
					name: 'address_input',
					type: 'text',
					placeholder: 'Enter your address'
				},
				validation: {
					required: true,
					email: false
				},
				valid: false,
				touched: false,
				validationMessage:'',
				showLabel: true
			},

			hours: {
				element: 'input',
				value: '',
				config: {
					label: 'Working hours',
					name: 'hours_input',
					type: 'text',
					placeholder: 'Enter working hours'
				},
				validation: {
					required: true,
					email: false
				},
				valid: false,
				touched: false,
				validationMessage:'',
				showLabel: true
			},

			phone: {
				element: 'input',
				value: '',
				config: {
					label: 'Phone',
					name: 'phone_input',
					type: 'text',
					placeholder: 'Enter phone'
				},
				validation: {
					required: true,
					email: false
				},
				valid: false,
				touched: false,
				validationMessage:'',
				showLabel: true
			},
			email: {
				element: 'input',
				value: '',
				config: {
					label: 'Working email',
					name: 'email_input',
					type: 'text',
					placeholder: 'Enter working email'
				},
				validation: {
					required: true,
					email: true
				},
				valid: false,
				touched: false,
				validationMessage:'',
				showLabel: true
			},
		}
  	}

  	submitForm = (event) => {
		event.preventDefault();
		let dataToSubmit = generateData(this.state.formData, 'site_info');
		let formIsValid = isFormValid(this.state.formData, 'site_info');
		if(formIsValid)
		{
			console.log(dataToSubmit);

		}
		else {
			this.setState({
				formError: true
			})
		}
	}

	updateForm = (element) => {

		const newFormdata = update(element, this.state.formData, 'site_info');
		this.setState({
			formError: false,
			formData: newFormdata
		})
	}


  	render() {
    	return (
      		<div>
        		<form onSubmit={(event)=> this.submitForm(event)} >
  					<h2>Personal Information</h2>

  					<div className='form_block_two'>
  						<div className='block'>
  							<FormField 
				      			id={'address'}
				      			formData={this.state.formData.address}
				      			change={(element)=> this.updateForm(element)}
				      		/>

				      	</div>
				      	<div className='block'>
				      		<FormField 
				      			id={'hours'}
				      			formData={this.state.formData.hours}
				      			change={(element)=> this.updateForm(element)}
				      		/>

				      	</div>

  					</div>
  					<FormField 
		      			id={'email'}
		      			formData={this.state.formData.email}
		      			change={(element)=> this.updateForm(element)}
		      		/>
		      		{
		      			this.state.formSuccess ?
		      			<div className='form_success'>Success</div>
		      			: null
		      		}
			      	{
		      			this.state.formError ?
		      			<div className='error_label'>
		      				Your email or password is incorrect, please check.
		      			</div>
		      			: null
	      		   	}
		      		<button onClick={(event)=> this.submitForm(event)}>
		      			Update personal information
		      		</button>

      			</form>
      		</div>
    	)
  	}
}

const mapStateToProps = (state) => {
	return {
		site: state.site
	}
}

export default connect(mapStateToProps)(UpdateSiteInfo)
